package cls_java;

public class Operators_bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 110;
		int c = a&b;
		int d = a|b;
		int e = a^b;//bitwise and
		
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
	}

}
