package cls_java;
import java.util.*;

public class Pstr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1 = Integer.toString(12);
		String str2 = Float.toString(21.2f);
		String str3 = Double.toString(8.2);
		String str4 = Byte.toString((byte) 9);
		String str5 = Boolean.toString(true);
		String str6 = Long.toString(3245);
		String str7 = Short.toString((short) 31);
		String str8 = Character.toString('A');
		System.out.println(str1);
		System.out.println(str2);
		System.out.println(str3);
		System.out.println(str4);
		System.out.println(str5);
		System.out.println(str6);
		System.out.println(str7);
		System.out.println(str8);
	}

}
