package lab;

public class Lab_3_p1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//pre increment operator
		int a = 25;
		int b = ++a;
		System.out.println(b);
		//post increment operator
		a = 25;
		b = a++;
		System.out.println(b);
		System.out.println(a);
		
	}

}
