package lab;

public class Lab_3_p3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//pre decrement operator
		int a = 30;
		int b = --a;
		System.out.println(b);
		System.out.println(a);
		//post decrement operator
		a = 30;
		b = a--;
		System.out.println(b);
		System.out.println(a);
						
	}

}
