package lab;

public class Lab_3_p7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 8;
		System.out.println("a++= "+ a++ +" --a= "+ --a +" ++a= "+ ++a);
		System.out.println(a);
		
		a=15;
		System.out.println("a--= "+ a-- +" ++a= "+ ++a +" a++= "+ a++ +" --a= "+--a);
		System.out.println(a);
	}

}
