package lab;

public class Lab_3_p8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int b = 10;
		System.out.println(a);
		a += 15;
		System.out.println(a);
		a -= 5;
		System.out.println(a);
		a *= b;
		System.out.println(a);
		System.out.println(b);
		a /= 10;
		System.out.println(a);
		a %= b;
		System.out.println(a);
		System.out.println(b);
		b <<= 2;
		System.out.println(b);
	}

}
